<?phpnamespace App\Controller\Admin;use App\Entity\Marque;use EasyCorp\Bundle\EasyAdminBundle\Config\Crud;use EasyCorp\Bundle\EasyAdminBundle\Controller\AbstractCrudController;use EasyCorp\Bundle\EasyAdminBundle\Field\ImageField;use EasyCorp\Bundle\EasyAdminBundle\Field\TextareaField;use EasyCorp\Bundle\EasyAdminBundle\Field\TextEditorField;use EasyCorp\Bundle\EasyAdminBundle\Field\TextField;use Vich\UploaderBundle\Form\Type\VichImageType;class MarqueCrudController extends AbstractCrudController{    public static function getEntityFqcn(): string    {        return Marque::class;    }    public function configureFields(string $pageName): iterable    {        $imageFile = TextareaField::new('imageFile')->setFormType(VichImageType::class);        $image = ImageField::new('image')->setBasePath('images/brand_images');        $fields = [            TextField::new('nom'),        ];        if ($pageName == Crud::PAGE_INDEX || $pageName == Crud::PAGE_DETAIL) {            $fields [] = $image;        } else {            $fields [] = $imageFile;        }        return $fields;    }}