<?php    namespace App\Controller;    use App\Controller\Admin\DashboardController;    use App\Controller\Admin\InterventionCrudController;    use App\Entity\Intervention;    use App\Entity\PrixInter;    use App\Entity\Produit;    use Doctrine\ORM\EntityManagerInterface;    use EasyCorp\Bundle\EasyAdminBundle\Config\Action;    use EasyCorp\Bundle\EasyAdminBundle\Router\AdminUrlGenerator;    use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;    use Symfony\Component\HttpFoundation\Request;    use Symfony\Component\HttpFoundation\Response;    use Symfony\Component\Routing\Annotation\Route;    class InterventionController extends AbstractController    {        private AdminUrlGenerator $adminUrlGenerator;        public function __construct(AdminUrlGenerator $adminUrlGenerator)        {            $this->adminUrlGenerator = $adminUrlGenerator;        }        public function tabPrixInter($id)        {            // retoure une tableau avec les valeurs des intervention et les prix pour un produit donnÃ©e            $interventions = $this->getDoctrine()->getRepository(Intervention::class)->findAll();            $produit = $this->getDoctrine()->getRepository(Produit::class)->find($id);            $tab = array();            foreach ($interventions as $intervention) {                $prixInter = $this->getDoctrine()->getRepository(PrixInter::class)->findByProdInter($id, $intervention->getId());                $prix = null;                $duree = null;                $id_prixInter = null;                if ($prixInter) {                    $prix = $prixInter->getPrix();                    $duree = $prixInter->getDuree();                    $id_prixInter = $prixInter->getId();                }                $tab [] = [                    'id_intervention' => $intervention->getId(),                    'nom_intervention' => $intervention->getNom(),                    'id_produit' => $produit->getId(),                    'id_prixInter' => $id_prixInter,                    'prix' => $prix,                    'duree' => $duree                ];            }            return $tab;        }        /**         * @Route("interventions-produit/{id}", name="interventions-produit")         */        public function interventionProduit($id, Request $request, EntityManagerInterface $entityManager)        {            $produit = $this->getDoctrine()->getRepository(Produit::class)->find($id);            $tabInter = $this->tabPrixInter($id);            if ($request->get('validation')) {                for ($x = 0; $x < count($request->get('prixInter')['prix']); $x++) {                    if ($request->get('prixInter')['prix'][$x]) {                        $prix = $request->get('prixInter')['prix'][$x];                        $duree = $request->get('prixInter')['duree'][$x];                        $intervention = $this->getDoctrine()->getRepository(Intervention::class)->find($request->get('prixInter')['id_intervention'][$x]);                        $prixInter = $this->getDoctrine()->getRepository(PrixInter::class)->find($request->get('prixInter')['id_prixInter'][$x]);                        if (!$prixInter) {                            $prixInter = new PrixInter();                        }                        if ($duree) {//                        dd($duree);                            $prixInter->setDuree(new \DateTime($duree));                        }                        $prixInter->setProduit($produit);                        $prixInter->setIntervention($intervention);                        $prixInter->setPrix($prix);                        $entityManager->persist($prixInter);                        $entityManager->flush();                    }                }                return $this->redirectToRoute('interventions-produit', ['id' => $id]);            }            return $this->render('admin/phone/interventions-produit.html.twig', [                'tabPrixInter' => $tabInter,                'produit' => $produit,            ]);        }        /**         * @Route("interUp/{id}/{redirectionVar}", name="interUp")         */        public function interUp($id,  EntityManagerInterface $entityManager, $redirectionVar = null)        {            $intervention = $this->getDoctrine()->getRepository(Intervention::class)->find($id);            $nOrderActuel = $intervention->getNOrder();            $precedent = $this->getDoctrine()->getRepository(Intervention::class)->findOneBy(['nOrder' => $nOrderActuel - 1]);            $nOrderPrecedent = $precedent->getNOrder();            $precedent->setNOrder($nOrderActuel);            $intervention->setNOrder($nOrderPrecedent);            $entityManager->persist($intervention);            $entityManager->flush();            $entityManager->persist($precedent);            $entityManager->flush();            if($redirectionVar == "customList"){                return $this->redirectToRoute('interventionOrderList');            }else{                $url = $this->adminUrlGenerator                    ->setDashboard(DashboardController::class)                    ->setController(InterventionCrudController::class)                    ->setAction(Action::INDEX)                    ->generateUrl();                return $this->redirect($url);            }             }        /**         * @Route("interDown/{id}/{redirectionVar}", name="interDown")         */        public function interDown($id,  EntityManagerInterface $entityManager, $redirectionVar = null)        {            $intervention = $this->getDoctrine()->getRepository(Intervention::class)->find($id);            $interventions = $this->getDoctrine()->getRepository(Intervention::class)->findAll();            $nOrderActuel = $intervention->getNOrder();            $suivant = $this->getDoctrine()->getRepository(Intervention::class)->findOneBy(['nOrder' => $nOrderActuel + 1]);            $nOrderSuivant = $suivant->getNOrder();            $suivant->setNOrder($nOrderActuel);            $intervention->setNOrder($nOrderSuivant);            $entityManager->persist($intervention);            $entityManager->flush();            $entityManager->persist($suivant);            $entityManager->flush();                if($redirectionVar == "customList"){                    return $this->redirectToRoute('interventionOrderList');                }else{                    $url = $this->adminUrlGenerator                        ->setDashboard(DashboardController::class)                        ->setController(InterventionCrudController::class)                        ->setAction(Action::INDEX)                        ->generateUrl();                    return $this->redirect($url);                }        }        /**         * @Route("interventionOrderList", name="interventionOrderList")         */        public function interventionOrderList()        {            $interventions  = $this->getDoctrine()->getRepository(Intervention::class)->findAll();            return $this->render('admin/phone/interventions-order-list.html.twig', [                'interventions' => $interventions            ]);        }        }