<?php    namespace App\Entity;    use App\Repository\NotesRepository;    use Doctrine\ORM\Mapping as ORM;    /**     * @ORM\Entity(repositoryClass=NotesRepository::class)     */    class Notes    {        /**         * @ORM\Id         * @ORM\GeneratedValue         * @ORM\Column(type="integer")         */        private $id;        /**         * @ORM\Column(type="text")         */        private $note;        /**         * @ORM\Column(type="datetime")         */        private $date;        /**         * @ORM\ManyToOne(targetEntity=User::class, inversedBy="notes")         * @ORM\JoinColumn(nullable=false)         */        private $user;        /**         * @ORM\ManyToOne(targetEntity=User::class, inversedBy="mesNotes")         * @ORM\JoinColumn(nullable=false)         */        private $auteur;        public function __construct(){            $this->setDate(new \DateTime());        }        public function getId(): ?int        {            return $this->id;        }        public function getNote(): ?string        {            return $this->note;        }        public function setNote(string $note): self        {            $this->note = $note;            return $this;        }        public function getDate(): ?\DateTimeInterface        {            return $this->date;        }        public function setDate(\DateTimeInterface $date): self        {            $this->date = $date;            return $this;        }        public function getUser(): ?User        {            return $this->user;        }        public function setUser(?User $user): self        {            $this->user = $user;            return $this;        }        public function getAuteur(): ?User        {            return $this->auteur;        }        public function setAuteur(?User $auteur): self        {            $this->auteur = $auteur;            return $this;        }        public function __toString()        {            return $this->note;        }    }